import React, {useState} from 'react'
import './styles/App.css'
import Table from './components/Table'
// import MyButton from './components/UI/button/MyButton.jsx'
import MyInput from './components/UI/input/MyInput.jsx'
import InputForm from './components/InputForm'
import MySelect from './components/UI/select/MySelect';

function App() {

  const [tableCells, setTableCells] = useState([
    {
      id: 1,
      date: "12/09/2021",
      title: "Bread",
      quantity: "5",
      distance: "12",
    },
    {
      id: 2,
      date: "15/09/2021",
      title: "Eggs",
      quantity: "10",
      distance: "5",
    },
    {
      id: 3,
      date: "18/09/2021",
      title: "Milk",
      quantity: "2",
      distance: "7",
    },
  ]);

  const [selectedColumn, setSelectedColumn] = useState('')
  const [searchQuery, setSearchQuery] = useState('')

  // const sorted

  const createNewCell = (newCell) => {
    setTableCells([...tableCells, newCell])
  }

  const sortColumns = (column) => {
    setSelectedColumn(column)
    setTableCells([...tableCells].sort((a, b) => a[column] > b[column])) //a[column].localeCompare(b[column]))) или а - b для цифр
  }

  return (
    <div className="App">
      <InputForm create={createNewCell} />
      <div>
        <MyInput value={searchQuery} onChange={e=> setSearchQuery(e.target.value)} placeholder="Сортировать по"/>
        <MySelect
          value={selectedColumn}
          onChange={sortColumns}
          defaultValue="Выбор колонки"
          options={[
            { value: "title", name: "Название" },
            { value: "quantity", name: "Количество" },
            { value: "distance", name: "Расстояние" },
          ]}
        />
        <MySelect
          value={selectedColumn}
          onChange={sortColumns}
          defaultValue="Выбор условия"
          options={[
            { value: "title", name: "Равно" },
            { value: "quantity", name: "Содержит" },
            { value: "distance", name: "Больше" },
            { value: "distance", name: "Меньше" },
          ]}
        />
      </div>
      <Table tableCells={tableCells} />
    </div>
  );
}

export default App;
