import React, {useState} from 'react'
import './styles/App.css'
import TableHead from './components/TableHead'
import MyButton from './components/UI/button/MyButton.jsx'
import MyInput from './components/UI/input/MyInput.jsx'

function App() {
  const [tableHeaders] = useState([
    { date: "Дата", name: "Название", quantity: "Количество", distance: "Расстояние" },
  ]);

  const [tableCells, setTableCells] = useState([
    { date: '', name: '', quantity: '', distance: '' }]);

const [title, setTitle] = useState('')
const [quantity, setQuantity] = useState("");
const [date, setDate] = useState("");
const [distance, setDistance] = useState("");

const addNewTableCell = (e) => {
  e.preventDefault()
  const newTableCell = {
    id: Date.now(),
    date,
    title,
    quantity,
    distance
  }
  setTableCells([...tableCells, newTableCell])
}

  return (
    <div className="App">
      <form>
        <MyInput
          type="text"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Дата"
        />
        <MyInput
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Название"
        />
        <MyInput
          type="text"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          placeholder="Количество"
        />
        <MyInput
          type="text"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
          placeholder="Расстояние"
        />
        <MyButton onClick={addNewTableCell}>Добавить</MyButton>
      </form>
      <TableHead tableHeaders={tableHeaders} title="Некие данные" />
    </div>
  );
}

export default App;
