import React, {useState} from 'react'
import './styles/App.css'
import Table from './components/Table'
import MyButton from './components/UI/button/MyButton.jsx'
import MyInput from './components/UI/input/MyInput.jsx'

function App() {
  const [tableHeaders, setTableHeaders] = useState([
    { id: 1, name: "Дата" },
    { id: 2, name: "Название" },
    { id: 3, name: "Количество" },
    { id: 4, name: "Расстояние" },
  ]);

const [title, setTitle] = useState('')
const [quantity, setQuantity] = useState("");

const addNewTableCell = (e) => {
  e.preventDefault()
  const newTableCell = {
    id: Date.now(),
    title,
    quantity
  }
  setTableHeaders([...tableHeaders, newTableCell])
}

  return (
    <div className="App">
      <form>
        <MyInput type="text" placeholder="Дата" />
        <MyInput
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Название"
        />
        <MyInput
          type="text"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          placeholder="Количество"
        />
        <MyInput type="text" placeholder="Расстояние" />
        <MyButton onClick={addNewTableCell}>Добавить</MyButton>
      </form>
      <Table tableHeaders={tableHeaders} title="Некие данные" />
    </div>
  );
}

export default App;
