import React, {useState} from 'react'
import './styles/App.css'
import Table from './components/Table'
import MyButton from './components/UI/button/MyButton.jsx'
import MyInput from './components/UI/input/MyInput.jsx'

function App() {
  const [tableHeaders] = useState([
    {id: 1, field: "Дата"},
    {id: 2, field: "Название"},
    {id: 3, field: "Количество"},
    {id: 4, field: "Расстояние" },
  ]);

  const [tableCells, setTableCells] = useState([
    { date: '12.06.2021', name: 'Bread', quantity: 5, distance: '12' }]);

const [title, setTitle] = useState('')
const [quantity, setQuantity] = useState("");
const [date, setDate] = useState("");
const [distance, setDistance] = useState("");

const addNewTableCell = (e) => {
  e.preventDefault()
  const newTableCell = {
    date,
    title,
    quantity,
    distance
  }
  setTableCells([...tableCells, newTableCell])
}

  return (
    <div className="App">
      <form>
        <MyInput
          type="text"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          placeholder="Дата"
        />
        <MyInput
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Название"
        />
        <MyInput
          type="text"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          placeholder="Количество"
        />
        <MyInput
          type="text"
          value={distance}
          onChange={(e) => setDistance(e.target.value)}
          placeholder="Расстояние"
        />
        <MyButton onClick={addNewTableCell}>Добавить</MyButton>
      </form>
      <Table tableHeaders={tableHeaders} title="Некие данные" />
    </div>
  );
}

export default App;
